objs.function:
if (hasError)
{
	if (!{$data:___checkResult}.focused)
	{
		webObj.focus();
		{$data:___checkResult}.focused = true;
	}
	webObj.css("background-color", "yellow");
	webObj.attr("title", errMsg);
	// 显示提示信息
	var msgSpan = _eterna.getWebObj(eg_temp.name + "_errMsg");
	msgSpan.text(errMsg);
}
else
{
	webObj.css("background-color", "");
	webObj.attr("title", "");
	var msgSpan = _eterna.getWebObj(eg_temp.name + "_errMsg");
	msgSpan.text("");
}
